close all; clear; clc;DataTM = load('DataTM.dat');DataTE = load('DataTE.dat');E_TM_FEKO = load('FEKOPlaneWave/E_TM.dat');E_TE_FEKO = load('FEKOPlaneWave/E_TE.dat');H_TM_FEKO = load('FEKOPlaneWave/H_TM.dat');H_TE_FEKO = load('FEKOPlaneWave/H_TE.dat');figure()hold onplot(DataTM(:,1), DataTM(:,2))plot(E_TM_FEKO(:,1), E_TM_FEKO(:,2), 'o')hold offfigure()hold onplot(DataTE(:,1), DataTE(:,2))plot(E_TE_FEKO(:,1), E_TE_FEKO(:,2), 'o')hold offfigure()hold onplot(DataTM(:,1), DataTM(:,3)*1E3)plot(H_TM_FEKO(:,1), H_TM_FEKO(:,2)*1E3, 'o')hold offfigure()hold onplot(DataTE(:,1), DataTE(:,3)*1E3)plot(H_TE_FEKO(:,1), H_TE_FEKO(:,2)*1E3, 'o')hold offDataHx_FEKO = load('FEKOPlaneWave/Hx.dat');DataHy_FEKO = load('FEKOPlaneWave/Hy.dat');DataHz_FEKO = load('FEKOPlaneWave/Hz.dat');